function App() {
  const [ form, setform ] = useState({
    name: '',
    lastName: '',
    phone: '',
    email: ''
  })

  const handleChange = (e) => {
    setform({
      ...form,
      [e.target.name]: e.target.value
    })
  }

  console.log(form);

  return (
