import { useState } from "react";

const useForm = (inicial) => {
    const [ form, setform ] = useState( inicial )

    const handleChange = (e) => {
        setform({
            ...form,
            [e.target.name]: e.target.value
        })
    }

    const reset = () => {
        form = {}
    }

    return [form, handleChange, reset]
}

export default useForm
