import {useState} from 'react';
import Input from "./components/Input";
import useForm from "./hooks/useForm";
import Card from "./components/Card"
import Container from "./components/Container"
import Button from "./components/Button"

function App() {
  const [usuarios, setUsuarios] = useState([])

  const [form, handleChange, reset] = useForm({ 
    name: '',
    lastName: '',
    phone: '',
    email: ''
  })

  const submit = (e) => {
    e.preventDefault()
    setUsuarios([
      ...usuarios,
      form
    ])
    reset()
  }

  console.log(form, usuarios);

  return (
    <div style={{marginTop: 50}}>
      <Container>
        <Card>
          <div style={{ padding: 20}}>
            <form onSubmit={submit}>
              <Input label='Nombre' name='name' value={form.name} onChange={handleChange}/>
              <Input label='Apellido' name='lastName' value={form.lastName} onChange={handleChange}/>
              <Input label='TelÃ©fono' name='phone' value={form.phone} onChange={handleChange}/>
              <Input label='Email' name='email' value={form.email} onChange={handleChange}/>
              <Button>Enviar</Button>
            </form>
          </div>
        </Card>
        <Card>
          <ul>
            {usuarios.map(u => <li key={u.email}>{`${u.name} ${u.lastName} ${u.phone} ${u.email}`}</li>)}
          </ul>
        </Card>
      </Container>
    </div>

  );
}

export default App;
